    public int tailleTerrain() {
        return carteTerrain.length;
    }

    public int codeCaseI(int i) {
        return carteTerrain[i];
    }

    public boolean estAutorisé(int x, int y) {
        return estAutorisé(x, y, false);
    }

    public boolean estAutorisé(int x, int y, boolean hydrophobe) {
        int[] autoriseTiles = hydrophobe ? CASE_AUTORISE_HYDROPHOBE : CASE_AUTORISE;
        int codeCase = codeCaseI(getIndiceCaseSousPosition(x, y));

        if (estDansTerrain(x, y)) {
            for (int autorise : autoriseTiles) {
                if (codeCase == autorise) {
                    return true;
                }
            }
        }
        return false;
    }

    public boolean estAutoriséIndice(int indice) {
        return estDansTerrainIndice(indice) && codeCaseI(indice) == WALKABLE_TILE;
    }

    public boolean estDansTerrain(int x, int y) {
        return x >= 0 && x < tailleLargeur * tailleTuile && y >= 0 && y < tailleHauteur * tailleTuile;
    }

    public boolean estDansTerrainIndice(int indice) {
        return indice >= 0 && indice < carteTerrain.length;
    }

    public int getIndiceCaseSousPosition(int x, int y) {
        return x / tailleTuile + (y / tailleTuile) * tailleLargeur;
    }

    public ArrayList<Integer> getIndicesAdjacent(int x, int y) {
        ArrayList<Integer> adjacents = new ArrayList<>();

        if (estDansTerrain(x, y)) {
            ajouterSiValide(adjacents, x + tailleTuile, y);
            ajouterSiValide(adjacents, x, y + tailleTuile);
            ajouterSiValide(adjacents, x - tailleTuile, y);
            ajouterSiValide(adjacents, x, y - tailleTuile);
            ajouterSiValide(adjacents, x + tailleTuile, y - tailleTuile);
            ajouterSiValide(adjacents, x + tailleTuile, y + tailleTuile);
            ajouterSiValide(adjacents, x - tailleTuile, y + tailleTuile);
            ajouterSiValide(adjacents, x - tailleTuile, y - tailleTuile);
        }

        return adjacents;
    }

    private void ajouterSiValide(ArrayList<Integer> adjacents, int x, int y) {
        if (estDansTerrain(x, y)) {
            adjacents.add(getIndiceCaseSousPosition(x, y));
        }
    }

    public ArrayList<Integer> getIndicesAdjacentsAutorises(int x, int y) {
        ArrayList<Integer> adjacents = getIndicesAdjacent(x, y);
        ArrayList<Integer> adjacentsAutorises = new ArrayList<>();

        for (int indice : adjacents) {
            if (estAutoriséIndice(indice)) {
                adjacentsAutorises.add(indice);
            }
        }
        return adjacentsAutorises;
    }

    public ArrayList<Integer> getIndicesAdjacentsAvecIndice(int indice) {
        int x = (indice % tailleLargeur) * tailleTuile;
        int y = (indice / tailleLargeur) * tailleTuile;
        return getIndicesAdjacentsAutorises(x, y);
    }

    public int getDirectionI1versI2(int indice1, int indice2) {
        int difference = indice1 - indice2;

        return switch (difference) {
            case -1 -> 6;
            case 1 -> 4;
            case 127 -> 9;
            case 128 -> 8;
            case 129 -> 7;
            case -127 -> 1;
            case -128 -> 2;
            case -129 -> 3;
            default -> 0;
        };
    }
